import React from 'react';
import styled from 'styled-components';

const ResumeContainer = styled.div`
  padding: 2rem;
  max-width: 800px;
  margin: auto;
  text-align: center;

  @media (max-width: 768px) {
    padding: 1rem;
  }
`;

const Title = styled.h1`
  font-size: 2rem;
  color: #333;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const Description = styled.p`
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const Button = styled.a`
  display: inline-block;
  padding: 0.7rem 1.5rem;
  font-size: 1rem;
  color: #fff;
  background-color: #61dafb;
  border: none;
  border-radius: 4px;
  text-decoration: none;
  margin: 0 1rem;
  margin-bottom: 1rem;
  cursor: pointer;

  &:hover {
    background-color: #21a1f1;
  }

  @media (max-width: 768px) {
    margin: 0.5rem;
    width: calc(100% - 1rem); // Ensure buttons take full width with spacing on mobile
  }
`;

const Resume = () => (
  <ResumeContainer>
    <Title>My Resume</Title>
    <Description>
      You can view or download my resume using the links below.
    </Description>
    <Button href="/resume.pdf" target="_blank">View Resume</Button>
    <Button href="/resume.pdf" download>Download Resume</Button>
  </ResumeContainer>
);

export default Resume;
