import React from 'react';
import styled from 'styled-components';
import Lottie from 'react-lottie';
import animationData from '../animations/data-science-animation.json';  // Make sure this path is correct

const AnimationWrapper = styled.div`
  width: 100%;
  max-width: 600px;
  margin: 2rem 0;

  @media (max-width: 768px) {
    max-width: 100%;
  }
`;

const Home = () => {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice'
    }
  };

  return (
    <div className="container">
      <h1 className="title">Welcome to My Data Science Portfolio</h1>
      <AnimationWrapper>
        <Lottie options={defaultOptions} />
      </AnimationWrapper>
      <p className="description">
        Discover my projects and skills in data science.
      </p>
    </div>
  );
};

export default Home;
