import React from 'react';
import { Helmet } from 'react-helmet';
import styled from 'styled-components';

const ResumeContainer = styled.div`
  padding: 2rem;
  max-width: 800px;
  margin: auto;
  text-align: left;
`;

const Title = styled.h1`
  font-size: 2rem;
  color: #333;
`;

const Button = styled.a`
  display: inline-block;
  padding: 0.7rem 1.5rem;
  font-size: 1rem;
  color: white;
  background-color: #61dafb;
  border: none;
  border-radius: 4px;
  text-decoration: none;
  margin-top: 1rem;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #21a1f1;
  }
`;

const Resume = () => (
  <ResumeContainer>
    <Helmet>
      <title>Resume - Data Science Portfolio</title>
    </Helmet>
    <Title>My Resume</Title>
    <p>You can view or download my resume using the links below.</p>
    <Button href={`${process.env.PUBLIC_URL}/pdf/resume.pdf`} target="_blank" rel="noopener noreferrer">
      View Resume
    </Button>
    <Button href={`${process.env.PUBLIC_URL}/pdf/resume.pdf`} download>
      Download Resume
    </Button>
  </ResumeContainer>
);

export default Resume;
